package com.fiap.fastfood.external.orm;

import com.amazonaws.services.dynamodbv2.datamodeling.*;
import lombok.NoArgsConstructor;
import lombok.Setter;

import java.util.Date;

@Setter
@NoArgsConstructor
@DynamoDBTable(tableName = "Orquestration")
public class OrquestrationORM {

    private String id;
    private String stepId;
    private String stepName;
    private String orderId;
    private Date createdAt;

    public OrquestrationORM(String id, String stepName) {
        this.id = id;
        this.stepName = stepName;
    }

    public OrquestrationORM(String id, String orderId, String stepName) {
        this.id = id;
        this.orderId = orderId;
        this.stepName = stepName;
    }

    @DynamoDBHashKey
    public String getId() {
        return id;
    }

    @DynamoDBRangeKey
    @DynamoDBAutoGeneratedKey
    public String getStepId() {
        return stepId;
    }

    @DynamoDBAttribute
    public String getStepName() {
        return stepName;
    }

    @DynamoDBAttribute
    public String getOrderId() {
        return orderId;
    }

    @DynamoDBAutoGeneratedTimestamp(strategy = DynamoDBAutoGenerateStrategy.CREATE)
    public Date getCreatedAt() {
        return createdAt;
    }

}
